from pylab import plot, showfrom math import exp# a function is given to us and we are asked to find actual value # and Euler approx# the exact solution, tdef f(t):    return 5*exp(t)/(4+exp(t))    delta_t = 0.1 # step-sizet = 0 # initial time t_list = [t]y_exact = [f(0)] for k in range(30): # 30 steps    t += delta_t        t_list.append(t)    y_exact.append(f(t))    plot (t_list,y_exact)## now doing Euler's approxfor k in range(3):        t = 0        y = 1                 t_list = [t]        y_list = [y]        delta_t = (.5)**k        n = 3*2**k                for k in range(n):            y_prime = 0.2*y*(5-y)                        delta_y = y_prime*delta_t                        t+= delta_t            y+= delta_y                        t_list.append(t)            y_list.append(y)                    plot(t_list,y_list)        show()